:5:"param";s:9:"post_type";s:5:"value";s:4:"post";s:8:"operator";s:2:"==";s:8:"order_no";i:0;s:8:"group_no";i:0;}]]></wp:metaValue></wp:postmeta><wp:postmeta><wp:metaKey>10000000x</wp:metaKey><wp:metaValue><![CDATA[a:14:{s:3:"key";s:9:"10000000x";s:5:"label";s:7:"Field 1";s:4:"name";s:7:"field_1";s:4:"type";s:4:"text";s:8:"order_no";i:0;s:12:"instructions";s:0:"";s:8:"required";s:1:"0";s:17:"conditional_logic";a:3:{s:6:"status";s:1:"0";s:5:"rules";a:1:{i:0;a:3:{s:5:"field";s:4:"null";s:8:"operator";s:2:"==";s:5:"value";s:0:"";}}s:8:"allorany";s:3:"all";}s:13:"default_value";s:0:"";s:11:"placeholder";s:0:"";s:7:"prepend";s:0:"";s:6:"append";s:0:"";s:10:"formatting";s:4:"html";s:9:"maxlength";s:0:"";}]]></wp:metaValue></wp:postmeta><wp:postmeta><wp:metaKey>20000000x</wp:metaKey><wp:metaValue><![CDATA[a:14:{s:3:"key";s:9:"20000000x";s:5:"label";s:7:"Field 2";s:4:"name";s:7:"field_2";s:4:"type";s:4:"text";s:8:"order_no";i:1;s:12:"instructions";s:0:"";s:8:"required";s:1:"0";s:17:"conditional_logic";a:3:{s:6:"status";s:1:"0";s:5:"rules";a:1:{i:0;a:3:{s:5:"field";s:4:"null";s:8:"operator";s:2:"==";s:5:"value";s:0:"";}}s:8:"allorany";s:3:"all";}s:13:"default_value";s:0:"";s:11:"placeholder";s:0:"";s:7:"prepend";s:0:"";s:6:"append";s:0:"";s:10:"formatting";s:4:"html";s:9:"maxlength";s:0:"";}]]></wp:metaValue></wp:postmeta><wp:postmeta><wp:metaKey>30000000x</wp:metaKey><wp:metaValue><![CDATA[a:14:{s:3:"key";s:9:"30000000x";s:5:"label";s:7:"Field 3";s:4:"name";s:7:"field_3";s:4:"type";s:4:"text";s:8:"order_no";i:2;s:12:"instructions";s:0:"";s:8:"required";s:1:"0";s:17:"conditional_logic";a:3:{s:6:"status";s:1:"0";s:5:"rules";a:1:{i:0;a:3:{s:5:"field";s:4:"null";s:8:"operator";s:2:"==";s:5:"value";s:0:"";}}s:8:"allorany";s:3:"all";}s:13:"default_value";s:0:"";s:11:"placeholder";s:0:"";s:7:"prepend";s:0:"";s:6:"append";s:0:"";s:10:"formatting";s:4:"html";s:9:"maxlength";s:0:"";}]]></wp:metaValue></wp:postmeta></item></channel></rss>]
		end
		it "renders php correctly" do
			# puts @static.to_php
			expect(@static.to_php).to eq %Q[<?php get_header(); ?>\n\n		<section>\n\n			<?php if(have_posts()) : while(have_posts()) : the_post(); ?><!-- start loop -->\n				<article>\n\n					<?php $field_1 = get_field("field_1"); if ($field_1): ?>\n						<?php echo $field_1; ?>\n					<?php endif; ?>\n					<?php $field_2 = get_field("field_2"); if ($field_2): ?>\n						<?php echo $field_2; ?>\n					<?php endif; ?>\n					<?php $field_3 = get_field("field_3"); if ($field_3): ?>\n						<?php echo $field_3; ?>\n					<?php endif; ?>\n\n				</article>\n			<?php endwhile; endif; ?><!-- end loop -->\n\n		</section>\n\n		<?php //get_sidebar(); ?><!-- optional -->\n\n<?php get_footer(); ?>]
		end
	end
end